import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
export default function AddEvent(){
    const navigate = useNavigate();
    const [date, setDate] = useState('');
    const [name, setName] = useState('');
    const [startTime, setStartTime] = useState('');
    const [endTime, setEndTime] = useState('');
    const [category, setCategory] = useState('');
    const [numVol, setNumVol] = useState('');
    const [location, setLocation] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        if (date === ''){
            setError('Invalid Email')
        }
        else if (name === ''){
            setError('Invalid Name')
        }
        else if (startTime === ''){
            setError('Invalid Address')
        }
        else if (endTime === ''){
            setError('Invalid Contact Details')
        }
        else if (category === ''){
            setError('Invalid Position')
        }
        else if (numVol === ''){
            setError('Invalid Position')
        }
        else if (location === ''){
            setError('Invalid Position')
        }
        else{
            navigate('/allevents')
        }
    }

    return(
        <div className="login-wrapper">
            <h1>Add Event</h1>
            <form onSubmit={handleSubmit}>
                <label>
                <p>Name <input type="text" onChange={e => setName(e.target.value)} /></p>
                </label>
                <label>
                <p>Date <input type="date" onChange={e => setDate(e.target.value)} /></p>
                </label>
                <label>
                <p>Start Time <input type="time" onChange={e => setStartTime(e.target.value)} /></p>
                </label>
                <label>
                <p>End Time <input type="time" onChange={e => setEndTime(e.target.value)} /></p>
                </label>
                <label>
                <p>Event category <input type="text" onChange={e => setCategory(e.target.value)} /></p>
                </label>
                <label>
                <p>No. of Volunteers Required <input type="number" onChange={e => setNumVol(e.target.value)} /></p>
                </label>
                <label>
                <p>Location <input type="text" onChange={e => setLocation(e.target.value)} /></p>
                </label>
                <div>
                <button type="submit">Submit</button>
                </div>
                {error && <div>{error}</div>}
            </form>

            <br />
            {/* <div>
                <button onClick={handleVolRegister}>Volunteer Registration</button>
                <button onClick={handleVolLogin}>Volunteer Login</button>
                <button onClick={handleAdminRegister}>Admin Registration</button>
            </div> */}
        </div>
    );
}
