import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from "react-router-dom";
import { Button, Card, CardContent, Divider, Typography } from '@mui/material';

export default function EventDetails(){
    const location = useLocation();
    const event_id = location.state.event_id;
    const navigate = useNavigate();
    const [eventDetails, seteventDetails] = useState({});
    const id = location.state.id;

    useEffect(() => {
        (async () =>{
        try{
            const response = await axios.post('https://z5xiwa5nre.execute-api.us-east-1.amazonaws.com/dev/getevent', {
            'id': event_id
        });
        seteventDetails(response.data.details)
        console.log(eventDetails)
        }
        catch{
            console.log("Error while retrieving")
        }
    })();
    })

    const handleLogout = () => {
        navigate('/')
    }

    const length = eventDetails.length
    console.log(length)
    return(
        <div className="centered-container" style={{backgroundColor: '#CCCCFF', display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', textAlign: 'center'}}>
            <Card style={{ width: '800px' , height: '530px', border: '1px solid black'}}>
                <CardContent>
                    <h1>EVENT DETAILS</h1>
                    <Divider style={{ margin: '16px 0', backgroundColor: 'black'}}/>
                    {eventDetails.length > 0 ? (
                    <div>
                        <p><b>Event Name: </b>{eventDetails.EventName.S}</p>
                        <p><b>Event Category: </b>{eventDetails.EventCategory.S}</p>
                        <p><b>Event Date: </b>{eventDetails.EventDate.S}</p>
                        <p><b>Event Time: </b>{eventDetails.EventTime.S}</p>
                        <p><b>Location: </b>{eventDetails.Location.S}</p>
                        <p><b>Event Hours: </b>{eventDetails.EventHours.S}</p>
                        <div>
                        {id ? (
                            <>
                                <p><b>Total Volunteers: </b>{eventDetails.TotalVolunteers.S}</p>
                                <p><b>Assigned Volunteers: </b>{JSON.stringify(eventDetails.AssignedVolunteers.M)}</p>
                                <p><b>Remaining Volunteers: </b>{eventDetails.RemainingVolunteers.S}</p>
                            </>
                        ) : (
                            <p></p>
                        )}
                        </div>
                        {/* Add more properties as needed */}
                    </div>
                    ) : (
                        <>
                            <p>Loading...</p>
                        </>
                    )}
                    <br/>
                    <Button variant="contained" style={{backgroundColor: '#CCCCFF', color: 'black', border: '1px solid black'}} fullWidth type="submit" onClick={handleLogout}>Logout</Button>
                </CardContent>
            </Card>
        </div>
    );
}
