import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from "react-router-dom";
import { Button, Card, CardContent, Divider, Typography } from '@mui/material';

export default function EventDetails(){
    const location = useLocation();
    const event_id = location.state.event_id;
    const navigate = useNavigate();
    const [eventDetails, seteventDetails] = useState({});

    useEffect(() => {
        (async () =>{
        try{
            const response = await axios.post('https://z5xiwa5nre.execute-api.us-east-1.amazonaws.com/dev/getevent', {
            'id': event_id
        });
        seteventDetails(response.data.details)
        console.log(eventDetails)
        }
        catch{
            console.log("Error while retrieving")
        }
    })();
    })

    const handleLogout = () => {
        navigate('/')
    }

    return(
        <div className="centered-container" style={{backgroundColor: '#CCCCFF', display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', textAlign: 'center'}}>
            <Card style={{ width: '800px' , height: '530px', border: '1px solid black'}}>
                <CardContent>
                    <h1>EVENT DETAILS</h1>
                    <Divider style={{ margin: '16px 0', backgroundColor: 'black'}}/>
                    <div>
                        <p>Event Name: {eventDetails.EventName.S}</p>
                        <p>Event Category: {eventDetails.EventCategory.S}</p>
                        <p>Event Date: {eventDetails.EventDate.S}</p>
                        <p>Event Time: {eventDetails.EventTime.S}</p>
                        <p>Location: {eventDetails.Location.S}</p>
                        <p>Event Hours: {eventDetails.EventHours.S}</p>
                        <p>Total Volunteers: {eventDetails.TotalVolunteers.S}</p>
                        <p>Assigned Volunteers: {JSON.stringify(eventDetails.AssignedVolunteers.M)}</p>
                        <p>Remaining Volunteers: {eventDetails.RemainingVolunteers.S}</p>
                        {/* Add more properties as needed */}
                    </div>
                    <br/>
                    <Button variant="contained" style={{backgroundColor: '#CCCCFF', color: 'black', border: '1px solid black'}} fullWidth type="submit" onClick={handleLogout}>Logout</Button>
                </CardContent>
            </Card>
        </div>
        // <div style={{textAlign: 'center', display: 'flex', justifyContent: 'center', backgroundColor: '#CCCCFF', height: '100vh'}}>
        //     <div style={{ border: '1px solid black', padding: '20px', marginTop: '8%', height: '50%', width: '40%', backgroundColor: 'white', color: 'black'}}>
        //         <h1>Event Details</h1>
        //         <hr />
