        //         {error && <div>{error}</div>}

        //         <div>
        //             <button onClick={handleLogout} style ={{backgroundColor: '#CCCCFF', boxShadow: '0px 2px 2px lightgray', border: '1px solid black'}}>Logout</button>
        //         </div>
        //     </div>
        // </div>
    );
}
